import { useSelector } from 'react-redux'
import ReactPaginate from 'react-paginate';
import "./DocTable.css"
import {Link} from "react-router-dom";
import React, { Component } from 'react';
import "./DocTable.css"
import {connect} from 'react-redux'


class DocTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            offset:0,
            data:[],
            perPage:10,
            currentPage:0
        };
    }
    data = this.props.docs

    recievedData = () =>{
        const data = this.data
        console.log("data in docTable:", data.length)
        const slice = data.slice(this.state.offset, this.state.offset + this.state.perPage)
        const postData = slice.map(doc => 
            <React.Fragment>

                <Link to={`document/${doc._id}`} state={{docSummary:doc.summary}}>
                    <h4>doc._id</h4>
                </Link>
                <p>doc.tags</p>
            </React.Fragment>)
       this.setState({
            pageCount:Math.ceil(data.length / this.state.perPage),
            postData
        })
    }

    handlePageClick = (page) => {
        const selectedPage = page.selected;
        const offset = selectedPage *this.state.perPage

       this.setState({
            currentPage: selectedPage,
            offset:offset
        }, () =>{
            this.recievedData()
        });
    }

    render() {
        return(<div>
            {this.state.postData}
            <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={this.state.pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={this.handlePageClick}
                containerClassName={"pagination"}
                activeClassName={"active"}/>
        </div>
        )}

}

const mapStateToProps = state => {
    console.log("state in docTable:", state.data)
    return {
        docs: state.data
    }
}

export default connect(mapStateToProps)(DocTable);
