import { useSelector } from 'react-redux'
import React, { Component } from 'react';
import ReactPaginate from 'react-paginate';
import "./DocTable.css"

function DocTable() {
    this.state = {
        offset:0,
        data:[],
        perPage:10,
        currentPage:0
    };
    var {docs} = useSelector(state => state.data) 

    handlePageClick = (page) => {
        const selectedPage = page.selected;
        const offset = selectedPage * this.state.perPage

        this.setState({
            currentPage: selectedPage,
            offset:offset
        }, () =>{
            recievedData(docs)
        });
    }

    return(
        <div>
            {this.state.postData}
            <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={this.state.pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={this.handlePageClick}
                containerClassName={"pagination"}
                activeClassName={"active"}/>
        </div>)
    }




function recievedData(data) {
    const slice = data.slice(this.state.offset, this.state.offset + this.state.perPage)
    const postData = slice.map(doc => 
        <React.Fragment>

            <Link to={`document/${docs._id}`} state={{docSummary:docs[doc].summary}}>
                <h4>doc._id</h4>
            </Link>
            <p>doc.tags</p>
        </React.Fragment>)
    this.setState({
        pageCount:Math.ceil(docs.length / this.state.perPage),
        postData
    })

}
export default DocTable;


