import './Chatbot.css'
import React, {useState} from "react";
import Chatbot from "react-chatbot-kit";
import 'react-chatbot-kit/build/main.css'
import config from "./chatbotConfig";
import MessageParser from "./MessageParser"
import ActionProvider from "./ActionProvider";
import Rephrase from "./Rephrase"
/*Chatbot UI component*/
function CBot() {
  const [showBot, toggleBot] = useState(false);

  const saveMessages = () => {
    const html = document.getElementsByClassName("react-chatbot-kit-chat-message-container") 
    if (html.item(0) == undefined) { console.log("bouncinsm,", html); return}
    console.log("saved:",html.item(0))
    localStorage.setItem('chat_messages', JSON.stringify(html[0].innerHTML));
  };
  const loadMessages = () => {
    const parsed = localStorage.getItem('chat_messages')
    const html = document.getElementsByClassName("react-chatbot-kit-chat-message-container")
    console.log("in storage:", parsed)
    console.log("h LM:",html.innerHTML)
    if (parsed == 'undefined' || html[0] == undefined ) { console.log("bouncinlm"); return}
    const messages = JSON.parse(localStorage.getItem('chat_messages'));
    document.getElementsByClassName("react-chatbot-kit-chat-message-container")[0].innerHTML = messages
    //return messages;
  };
  return (
    <div  className="CBot">
      {showBot && (<Chatbot
       config={config}
        messageParser={MessageParser}
        actionProvider={ActionProvider}
        messageHistory={loadMessages()}
        saveMessages={saveMessages()}
      />)}
       <button onClick={() => toggleBot((prev) => !prev)}>Bot</button>
    </div>
  );
}



export default CBot;
