import "./Document.css"
import { useLocation } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import React, {useEffect} from 'react'
import { loadDocument} from '../../model/actions';
export default function Document() {
   /* docSum
    docId
    docFull*/
  //  const productData = [ ... ];
 // const { url } = useRouteMatch();
   // var docID = props.state
   const location = useLocation()
  const { docID,docSummary} = location.state

    //get summary
    //get full doc
   // var full = ""
    const dispatch = useDispatch();
    var {doc} = useSelector(state => state.data)
    var docFull = ""
    useEffect(() => {
        dispatch(loadDocument({docID}));
        if (doc) {

          console.log("DOC in Document.js:",doc['fullText'])
        }
        docFull = doc['fullText']
    }, []) //.then(response => console.log("response!:",response.length))


    return ( 
        <div className="DocPage">
            <h1>{docID} is here!!</h1>
            <div className="DocSummary">{docSummary}</div>
            <div className="FullText">{docFull}</div>
        </div>

    )
}
