import React, {useEffect} from 'react'
import { MDBContainer, MDBRow, MDBCol } from 'mdb-react-ui-kit';
///import {docs} from "../../model/reducer"
import "./DocGrid.css"
import { useDispatch, useSelector } from 'react-redux'
import {Link} from "react-router-dom";
import { loadDocument} from '../../model/actions';


var docFull = ""

export default function DGrid() {
    var {docs} = useSelector(state => state.data)
   // path = "/document:" + docs[doc]._id

  return (
    <div className="grid">
        <span onClick={this.getFullText(docs[doc]._id)}>
        {docs && Object.keys(docs).map((doc,i) => (
            <Link to='document' state={{docID:docs[doc]._id,docSummary:docs[doc].summary}}
           /*to={`document/:${docs[doc]._id}`} 
           {{
                pathname:"document",
                state:"lol"
            }}*/ >
                <tbody key={i} className="cell" >
                    <tr>
                        <td ><b>{docs[doc]._id}</b></td>
                        <td>{docs[doc].tags[0]}</td>
                    </tr>
                </tbody>
            </Link>))}
            </span>
    </div>
  );
}

function getFullText(docID) {
    const dispatch = useDispatch();
    var {doc} = useSelector(state => state.data)
    useEffect(() => {
        dispatch(loadDocument({docID}));
        if (doc) {
          console.log("DOC in DocGrid.js:",doc['fullText'])
        }
        docFull = doc['fullText']
    }, [])
}
