   const location = useLocation()
  const { docID,docSummary} = location.state
    console.log("DOC PROPS:",docID)
    //get summary
    //get full doc
    var full = ""
    const dispatch = useDispatch();
    var {doc} = useSelector(state => state.data)
    useEffect(() => {
        dispatch(loadDocuments({docID}));
        if (doc) {
          full = doc.fullText
        }
    }, []) //.then(response => console.log("response!:",response.length))


    return ( 
        <div className="DocPage">
            <h1>{docID} is here!!</h1>
            <div className="DocSummary">{docSummary}</div>
            <div className="FullText">{full}</div>
        </div>

    )
}
