//class which parses through user input in the chatbot
//'The simplest version is to check for keywords with javascript .includes()'
class MessageParser {
    constructor(actionProvider, state) {
      this.actionProvider = actionProvider;
      this.state = state;
    }

    parse(message) {
      console.log("message:",message)

      const lowercase = message.toLowerCase()
      if (lowercase.includes('hello')) {
        this.actionProvider.greet();
      }
      if (this.state.DocumentSearch == null){
        if (lowercase.includes('other')) {
          this.state.DocumentSearch = false
        }
        else if (lowercase.includes('document')) {
          this.state.DocumentSearch = true
        }
      }
      else if (this.state.DocumentSearch == false){
        this.actionProvider.handleOther(message);
      }



    }


  }

  export default MessageParser;
