 // ActionProvider starter code
 class ActionProvider {
   constructor(
    createChatBotMessage,
    setStateFunc 
    /*createClientMessage,
    stateRef,
    createCustomMessage,
    ...rest*/
  ) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
    /*this.createClientMessage = createClientMessage;
    this.stateRef = stateRef;
    this.createCustomMessage = createCustomMessage;*/
  }
  addMessageToState = (message) => {
   this.setState((prevState) => ({
     ...prevState,
     messages: [...prevState.messages,message],
   }));
  };

  greet = () => {
   const message = this.createChatBotMessage("Hello!")
   this.addMessageToState(message) //adds message to ui
  }

  //handlers
  handleDocuments = () => {
   const message = this.createChatBotMessage("docs not supported atm")
   this.addMessageToState(message) //adds message to ui
  }
  /*
    this.setState((state) => ({
      ...state,
     DocumentSearch:false
    }))*/
   handleOtherSetup = () => {
     this.setState((state) => ({
       ...state,
      DocumentSearch:false
     }))
     const message = this.createChatBotMessage("Enter your question:")
    this.addMessageToState(message)
   }
  handleOther = (question) => {
   this.setState((state) => ({
     ...state,
    OtherQuestion:question
   }))
    const message = this.createChatBotMessage("Answer found:",
    {
      widget:"Other"
    }
    )
    this.addMessageToState(message)
   }


  handleOtherQuiz = () => {
   const message = this.createChatBotMessage("Q's found:", 
   {
     widget:"Quiz"
   });
  this.addMessageToState(message)
 }

}
//use the setState() method to change the state object. 
//It ensures that the component has been updated and calls for re-rendering of the component.
export default ActionProvider;
