import { useSelector } from 'react-redux'
import ReactPaginate from 'react-paginate';
import "./DocTable.css"
import {Link} from "react-router-dom";
import React, { useState } from 'react';
import "./DocTable.css"

export var showDocs = false;

export default function DocTable({setShow, updatePageState}) {
    //setShow(true)
   // updatePageState()
    const [page, setPage] = useState(0);
    var {docs} = useSelector(state => state.data)
    const docsPerPage = 5
    const docsSeen = page * docsPerPage
    const displayedDocs = docs.slice(
        docsSeen, docsSeen + docsPerPage
    ).map((doc) => {
        function setIsShown(hover) {
            var text = ""
            if (hover) {
                text = doc.tags.slice(0,10).toString(", ")
            }
            else {
                text = doc.summary
            }
        }
        return (
            <div className='docDiv'>

            <React.Fragment >

            <Link to={`document/${doc._id}`} state={{docSummary:doc.summary}}>
                <p className="title"  onMouseEnter={() => setIsShown(true)}
        onMouseLeave={() => setIsShown(false)}>{doc._id}</p>
            </Link>
            <p className="tags">{text}...</p>
        </React.Fragment>
        </div>
        )
    })
    //console.log("Table | docsSeen", docsSeen)
    const totalPages= Math.ceil(docs.length/ docsPerPage)

    const changePage = ({selected}) => {
        setPage(selected);
    }
    return(
        <div>{displayedDocs}
        <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={totalPages}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={changePage}
                containerClassName={"pagination"}
                activeClassName={"active"}/>
        </div>)
}
